USE DB_MAGAZZINO;

SET FOREIGN_KEY_CHECKS=0;

CREATE or REPLACE DATABASE DB_MAGAZZINO;

USE DB_MAGAZZINO;

CREATE TABLE tecnico (
  id int NOT NULL PRIMARY KEY AUTO_INCREMENT,
  cognome varchar(255),
  nome varchar(255),
  targa varchar(255)
);

CREATE TABLE prodotto (
  id int NOT NULL PRIMARY KEY AUTO_INCREMENT,
  codice varchar(255),
  descrizione varchar(255),
  quantita int(255),
  stato varchar(255)
);

CREATE TABLE tracciamento (
  id int NOT NULL PRIMARY KEY AUTO_INCREMENT,
  id_prodotto int(255),
  id_tecnico int(255),
  quantita int(255),
  stato varchar(10),
  timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

ALTER TABLE tracciamento ADD INDEX index_prodotto (id_prodotto);
ALTER TABLE tracciamento ADD INDEX index_tecnico (id_tecnico);

ALTER TABLE tracciamento ADD CONSTRAINT fk_prodotto_id FOREIGN KEY (id_prodotto) REFERENCES prodotto(id);
ALTER TABLE tracciamento ADD CONSTRAINT fk_tecnico_id FOREIGN KEY (id_tecnico) REFERENCES tecnico(id);










                                                                                                                
DELIMITER $$
DROP TRIGGER IF EXISTS tracciamento_verso_prodotto_ins$$
CREATE TRIGGER `tracciamento_verso_prodotto_ins` AFTER INSERT ON tracciamento FOR EACH ROW
BEGIN
IF  ((Select quantita FROM prodotto where id = NEW.id_prodotto) + (SELECT quantita  FROM tracciamento WHERE id = (SELECT MAX(id) FROM tracciamento ))) >= 0 THEN
  UPDATE prodotto SET quantita = (quantita + (SELECT quantita FROM tracciamento WHERE id =(SELECT MAX(id) FROM tracciamento))) WHERE id = NEW.id_prodotto;
ELSE 
 signal sqlstate '45000' set message_text = 'My Error Message insert';
END IF;
END$$
DELIMITER ;


